pub enum ColorValue {
    Off = 0,
    VeryLow = 42,
    Low = 84,
    MediumLow = 126,
    Medium = 168,
    MediumHigh = 210,
    High = 252,
    Full = 255
}

impl ColorValue {
    pub fn size(&self) -> u8 {
        match self {
            ColorValue::Off => 0,
            ColorValue::VeryLow => 1,
            ColorValue::Low => 2,
            ColorValue::MediumLow => 3,
            ColorValue::Medium => 4,
            ColorValue::MediumHigh => 5,
            ColorValue::High => 6,
            ColorValue::Full => 7
        }
    }
}

impl From<u8> for ColorValue {
    fn from(value: u8) -> ColorValue {
        match value {
            0..=21 => ColorValue::Off,
            22..=63 => ColorValue::VeryLow,
            64..=106 => ColorValue::Low,
            107..=149 => ColorValue::MediumLow,
            150..=192 => ColorValue::Medium,
            193..=235 => ColorValue::MediumHigh,
            236..=253 => ColorValue::High,
            254..=255 => ColorValue::Full
        }
    }
}

pub struct OutColor(u8, u8, u8);


pub struct Color(pub u8, pub u8, pub u8);

impl Color {
    fn index(&self) -> u32 {
        256*256*self.0 as u32 + 256*self.1 as u32 + self.2 as u32
    }
}

struct RGBColor {
    red: u8,
    green: u8,
    blue: u8
}

